mapper.select:
SELECT p.p_name,p.p_sex,p.p_birthday,p.p_img,r.r_id,r.r_date,r.r_status,r.r_info,r.r_remark
FROM registration r LEFT JOIN patient p on r.p_id = p.p_id
WHERE r.d_id = #{dId} and r.r_status = #{rStatus}

	select 

  from registration
  where p_id = #{pId} and r_date = #{dateTime}

  	select COUNT(*)
	from registration
	where DATE(r_date) = #{rDate} and d_id = #{tId}

  	select COUNT(*)
	from registration
	where d_id = #{tId}
